    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
       String cpf = txtCpf.getText(); 
       String nome = txtNome.getText();
       char sexo = txtSexo.getText().charAt(0);
       int idade = Integer.parseInt(txtIdade.getText());
       
       pessoa = new Pessoa(cpf, nome, sexo, idade);
       lstPessoa.add(pessoa);
       JOptionPane.showMessageDialog(rootPane, nome+" CADASTRADO COM SUCESSO!");
    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        String str =" ";
        for (int i =0; i<lstPessoa.size();i++){
            str+= "\n===========================\n";
            str += lstPessoa.get(i).imprimir();
        }
        JOptionPane.showMessageDialog(rootPane,str);
    }//GEN-LAST:event_btnMostrarActionPerformed

